import {combineReducers} from 'redux';
import { FETCH_POSTS } from './actions';
import axios from 'axios'
import { fetchData} from './actions';




const initialState = {
    books: [],
    currentPage: 1,
    limit: 5,
    totalBooksCount: 0,
    gotResponse:false
}

const booksReducer = async (state = initialState, action) => {
    switch(action.type){

        case FETCH_POSTS:{
            
            const response = await axios.get(action.payload)
                return {...state, books: response.data,gotResponse:true}

            
        }
        default: {
            return state;
        }
       
    }
}



export const postsReducer = combineReducers({allbooks: booksReducer});
