import React, { useContext } from 'react';
import{NavLink, useHistory} from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import Nav from 'react-bootstrap/Nav'
import 'bootstrap/dist/css/bootstrap.css';
import '../components/Navbar.css'


export const Navbar = () =>{
    const history = useHistory();
    const auth = useContext(AuthContext);
    const logoutHendler = event =>{
        event.preventDefault();
        auth.logout();
        history.push('/')
    }
    return(
    <Nav className = "nav ">
        
        <Nav.Item className = "navitem">
             <Nav.Link as={NavLink} eventKey="link-1" className = "link" to="/allbooks">
                             AllBooks
                 </Nav.Link>
        </Nav.Item>
        <Nav.Item>
        <Nav.Link as={NavLink} eventKey="link-2" className = "link" to="/book">
                Books
                </Nav.Link>
        </Nav.Item>
        
        <Nav.Item>
        <Nav.Link as={NavLink} href="/"  className = "link" onClick ={logoutHendler} eventKey="link-3">LogOut</Nav.Link>
        </Nav.Item>
    </Nav>
        
    )
}
