import {combineReducers} from 'redux';
import { FETCH_POSTS} from './actions';
import axios from 'axios'

const initialState = {
    books: [
       
    ],
}

const booksReducer = (state = initialState, action) => {
    switch(action.type){
        case FETCH_POSTS:{
            axios.get("http://localhost:5000/api/auth/books").
            then(res=>{
               state = res.data
            })
            .catch((error) => {
                console.log(error)
            })
        }
       default: state
    }
}



export const postsReducer = combineReducers({list: booksReducer});
