import React, { useEffect} from 'react';
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import BookBox from "../components/BookBox";
import Row from 'react-bootstrap/Row';
import Pagination from 'react-bootstrap/Pagination'
import 'bootstrap/dist/css/bootstrap.min.css';
import Carousel from 'react-bootstrap/Carousel'
import Img1 from "../CaruselImg/Img1.jpg"
import Img2 from "../CaruselImg/Img2.jpg"
import Img3 from "../CaruselImg/Img3.jpg"
import { getBooksThunk, getBookCountThunk, updateCurrentPage } from '../redux/booksSlice'
import {users} from "../redux/userSlice"
import "../index.css"

export const Books = () =>{
    const books = useSelector(state => state.books.books);
    const limit = useSelector(state => state.books.limit)
    const pageCount = useSelector(state => state.books.pageCount)
    const currentPage = useSelector(state => state.books.currentPage)
    const dispatch = useDispatch();
    const verify = useSelector(state => state.users.token)
    useEffect(() => { dispatch(getBookCountThunk(verify)) }, [])
    useEffect(() => { dispatch(getBooksThunk(currentPage, limit,verify)) }, [currentPage])
    
    const handleChangePage = (newPage) => {
        dispatch(updateCurrentPage(newPage))
    }
    
    let pages = []
    for(let i = 1; i <= pageCount; i++) {
        pages.push(
            {
                number: i,
                ref: `./books?page=${i}`
            }
        )
    }
    const handlecheck = (e) => {
         
         if(e.target.checked == true){
             chaked = true;
            console.log(e.target.value)
         }
    }
    return(
        <div className = "container-fluid bookscont">
             <div style={{ display: 'block', width: "80%", padding: 30 , margin: "0 auto"}}>
                <Carousel interval={2000}>
                <Carousel.Item style={{'height':"400px"}}>
                    <img
                    className="d-block w-100"
                    src= {Img1}
                    
                    height = {400}
                    alt="First slide"
                    />
                    <Carousel.Caption>
                   
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item style={{'height':"400px"}}>
                    <img
                    className="d-block w-100"
                    src={Img2}
                    width = {500}
                    height = {400}
                    alt="Second slide"
                    />

                    <Carousel.Caption>
                  
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item style={{'height':"400px"}}>
                    <img
                    className="d-block w-100"
                    src= {Img3}
                    width = {500}
                    height = {400}
                    alt="Third slide"
                    />

                    <Carousel.Caption>
                
                    </Carousel.Caption>
                </Carousel.Item>
                </Carousel>
                </div>
                <div className = "container-fluid">
                    <input type = "text " placeholder =" " ></input>
                    <div className = "form-check form-check-inline">
                            <input className ="form-check-input" type="checkbox" id="inlineCheckbox1" checked ={false} onChange ={handlecheck} value="1"/>
                            <label className ="form-check-label" >To - max</label>
                            </div>
                            <div className ="form-check form-check-inline">
                            <input className ="form-check-input" type="checkbox" id="inlineCheckbox2" checked = {false} onChange ={handlecheck} value="-1"/>
                            <label className ="form-check-label" >To - min</label>
                    </div>
                </div>
            <Row xs={1} md={2} lg={3} className="g-4">
                { books && books.length > 0 && books.map(book => {
                    return(
                            <div>
                                <BookBox book = {book} key = {book._id}/> 
                            </div>
                    )
                }) }
            </Row>
            <div style={{ display: "flex", justifyContent: "center" }}>
                {pages.map(page => 
                    <Pagination className="pagination pagination-sm"> 
                        <Pagination.Item className = "page-item">
                            <Link to={page.ref} className="page-link" onClick={() => handleChangePage(page.number)}>
                        {page.number}
                        </Link> </Pagination.Item> 
                    </Pagination>
                
                )}
            </div>
        </div>
    )   
}
