import React from 'react';
import { useSelector, useDispatch,} from "react-redux";
import { useEffect} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';


export const Favorites = () =>{
    const dispatch = useDispatch()
    let token = useSelector(state => state.users.token)
    useEffect(() => { dispatch(getFavoritesThunk(token)) }, [])
    const favorites = useSelector(state => state.books.favoritebooks)
    console.log(favorites)
    return(
      <div className = "container - fluid">
         <Col>
             <Card style={{ width: '18rem' }}>
                <Card.Body>
                  <Link to = {`/books/${book._id}`}><Card.Title >{book.title}</Card.Title></Link>
                 <Card.Subtitle className="mb-2 text-muted">{book.author}</Card.Subtitle>
             <Accordion defaultActiveKey="0">
                <Accordion.Item eventKey="1">
                <Accordion.Header>About the book</Accordion.Header>
                    <Accordion.Body>
                                    {book.description}
                    </Accordion.Body>
                </Accordion.Item>
                </Accordion> 
             </Card.Body>
         </Card>
     </Col>

      </div>
    )
}
