    //     history.push('/your-route');
    // };
    return(
    <Col>
         <Card style={{ width: '18rem' }}>
             <Card.Body>
             <Link to={`/book/${book._id}`} onClick = {toBookLink}><Card.Title>{book.title}</Card.Title></Link>
             <Card.Subtitle className="mb-2 text-muted">{book.author}</Card.Subtitle>
             <Card.Text>
                  {book.description}
             </Card.Text>
             </Card.Body>
         </Card>
     </Col>
    )
}
