import {combineReducers} from 'redux';
import { FETCH_POSTS, CHANGE_BOOKS } from './actions';
const axios = require('axios')



const initialState = {
    books: [
       
    ]
}

const booksReducer = async (state = initialState, action) => {
    switch(action.type){
        case FETCH_POSTS:{
          const response = await fetch('http://localhost:5000/api/auth/books')
                .then((response) => {
                    alert(response.json)
                 return response.json();
                 
             })
                 .then((data) => {
                 console.log(data);
             });
            return{...state, books: [...state.books, response]}
        }
        default: {
            return state;
        }
    }
}



export const postsReducer = combineReducers({allbooks: booksReducer});
