import React, { useState , useEffect} from 'react';
import {useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import BookBox from "../components/BookBox";
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col'
import 'bootstrap/dist/css/bootstrap.min.css';
import {getBooksThunk, getBookCountThunk} from '../redux/actions'
import ReactPaginate from 'react-paginate'






export const AllBooksPage = () =>{
    const books = useSelector(state => state.books.books);
    const page = useSelector(state => state.books.page)
    const limit = useSelector(state => state.books.limit)
    const pageCount = useSelector(state => state.books.pageCount)
    const dispatch = useDispatch();

    useEffect(() => {dispatch(getBooksThunk(page, limit))}, [])
    useEffect(() => {dispatch(getBookCountThunk())}, [])
    
      
    return(
    <div className = "container-fluid">
        <ReactPaginate 
            peviousLabel = {'prev'}
            nextLabel = {'next'}
            breakLabel = {'...'}
            pageCount = {pageCount}
            pageRangeDisplayed = {5}
            marginPagesDisplayed = {3} />
         <Row xs={1} md={2} lg={3} className="g-4">
            { books && books.length>0 && books.map(book => {
                   return(
                   <div>
                   <BookBox book = {book} key = {book._id}/> 
                   <Link to="/book/$book"  className="btn btn-primary">Sign up</Link>
                   </div>
                   )
             }) }
         
         </Row>
     
     
    </div>
      
    )   
}
