import {combineReducers} from 'redux';
import { UPDATE_BOOKS } from './actions';

const initialState = {
    books: [],
    currentPage: 1,
    limit: 5,
    totalBooksCount: 0,
    gotResponse:false
}

const booksReducer = async (state = initialState, action) => {
    switch(action.type){
        case UPDATE_BOOKS:{
            // const response = await axios.get(action.payload)
            //     return {...state, books: response.data,gotResponse:true}
            alert("Books: " + JSON.stringify(action.payload.books))
            return {...state, books:action.payload.books}   
        }
        default: {
            return state;
        }
       
    }
}





export const postsReducer = combineReducers({books: booksReducer});
