import React, { useEffect} from 'react';
import {useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import BookBox from "../components/BookBox";
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col'
import 'bootstrap/dist/css/bootstrap.min.css';
import {getBooksThunk, getBookCountThunk, updateCurrentPageThunk} from '../redux/actions'
import ReactPaginate from 'react-paginate'






export const AllBooksPage = () =>{
    const books = useSelector(state => state.books.books);
    const limit = useSelector(state => alert(JSON.stringify(state)))
    const pageCount = useSelector(state => state.books.pageCount)
    const currentPage = useSelector(state => state.books.currentPage)
    const dispatch = useDispatch();

    useEffect(() => {dispatch(getBooksThunk(currentPage, limit))}, [])
    useEffect(() => {dispatch(getBookCountThunk())}, [])

    // const handleChangePage = (e) => {
    //     const selectedPage = e.selected;
    //     dispatch(updateCurrentPageThunk(selectedPage + 1, limit))
    // }
    
    let pages = []
    for(let i = 1; i < pageCount; i++) {
        pages.push(i)
    }
      
    return(
    <div className = "container-fluid">
        {/* <ReactPaginate 
            peviousLabel = {'prev'}
            nextLabel = {'next'}
            breakLabel = {'...'}
            pageCount = {pageCount}
            pageRangeDisplayed = {3}
            marginPagesDisplayed = {2} 
            onPageChange={handleChangePage}/> */}
         <Row xs={1} md={2} lg={3} className="g-4">
            { books && books.length>0 && books.map(book => {
                   return(
                   <div>
                   <BookBox book = {book} key = {book._id}/> 
                   <Link to="/book/$book"  className="btn btn-primary">Sign up</Link>
                   </div>
                   )
             }) }
         
         </Row>
    
        {pages.map((page) => <div onClick={() => {dispatch(updateCurrentPageThunk(page, limit))}}>{page}</div>)}
     
     
    </div>
      
    )   
}
