import axios from 'axios'

export const UPDATE_BOOKS = "BOOKS/UPDATE_BOOKS"
export const UPDATE_BOOKS_COUNT = "BOOKS/UPDATE_BOOKS_COUNT"

function updateBooks(newBooks) {
    return {type: UPDATE_BOOKS, payload: newBooks}
}


export const getBooksThunk = (page, limit) => {
    return (dispatch) => {
        axios.get(`http://localhost:5000/api/auth/books?page=${page}&limit=${limit}`)
            .then((res) => dispatch(updateBooks(res.data)))
            .catch(error => alert('Error getting books'))
    }
}
function updatBookCount (newBooksCount){
    return {type: UPDATE_BOOKS_COUNT, payload: newBooksCount}
}

export const getBookCount = () => {
    return (dispatch) => {
        axios.get(`http://localhost:5000/api/auth/bookcount`)
            .then((res) => dispatch(updateBookCount()))
            .catch(error => alert('Error update count'))
    }
}


