import {combineReducers} from 'redux';
import {ADD_BOOK, FETCH_POSTS} from './actions';





const initialState = {
    books: [
       
    ],
}


const booksReducer = (state = initialState, action) => {
    switch(action.type){
        case FETCH_POSTS:{
            return [ action.payload.data, ...state ]
        }
        default: return state
    }
}



export const postsReducer = combineReducers({list: booksReducer});
