import {combineReducers} from 'redux';
import { FETCH_POSTS} from './actions';


const initialState = {
    books: [
       
    ],
}

const booksReducer = () => (state = initialState, action) => {
    switch(action.type){
        case FETCH_POSTS:
            return [ ...action.payload];
        default:
            return state;
      
    }
}



export const postsReducer = combineReducers({list: booksReducer});
