import React from "react";
import { useState , useRef} from "react";
import { useSelector , useDispatch } from 'react-redux'
import Card from 'react-bootstrap/Card'
import 'bootstrap/dist/css/bootstrap.min.css';
import Accordion from 'react-bootstrap/Accordion'
import InputGroup from 'react-bootstrap/InputGroup'
import Button from 'react-bootstrap/Button'
import FormControl from 'react-bootstrap/FormControl'
import "../index.css"

import { withRouter } from 'react-router-dom'
function Book(props) {
    const [word,setWord] = useState("")
    const inputRef = useRef()
    const input_Ref = useRef()
    
    const dispatch = useDispatch();


    const bookId = props.match.params.id
    const books = useSelector(state => state.books.books)
    const book = books.find(book => book._id === bookId)
    const handleWord = () =>{
         const inputValue =  inputRef.current.value
         

    }
    const handleMail = () => {
        input_Ref.current.value
    }
    return(
        <div className = "container-fluid bookcase">
            <div className = "row">
            <div className = "col-md-6">
         <Card style={{ width: '18rem' , display: "block"}}>
             <Card.Body>
             <Card.Title>{book.title}</Card.Title>
             <Card.Subtitle className="mb-2 text-muted">{book.author}</Card.Subtitle>
             <Accordion defaultActiveKey="0">
                <Accordion.Item eventKey="1">
                <Accordion.Header>About the book</Accordion.Header>
                     <Accordion.Body>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
                        tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
                        veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
                        commodo consequat. Duis aute irure dolor in reprehenderit in voluptate
                        velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                        cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id
                        est laborum.
                     </Accordion.Body>
                 </Accordion.Item>
              </Accordion> 
             </Card.Body>
         </Card>
         </div>
        <div className = "col-md-6">
         <InputGroup className="mb-3 inputgroup">
              <InputGroup.Text  id="inputGroup-sizing-default">Your Comment</InputGroup.Text>
              <Button onClick = {handleWord} variant="secondary">Submit</Button>
    

              <FormControl ref ={inputRef} onChange = {e => setWord(e.target.value)} value = {word}
                aria-label="Default"
                aria-describedby="inputGroup-sizing-default"
              />
              <InputGroup.Text  id="inputGroup-sizing-default">Your Mail</InputGroup.Text>
              <Button onClick = {handleMail} variant="secondary">Submit</Button>
    

              <FormControl ref ={input_Ref} onChange = {e => setWord(e.target.value)} value = {word}
                aria-label="Default"
                aria-describedby="inputGroup-sizing-default"
              />
              </InputGroup>
              </div>
         </div>
        </div>
    )
}

export default withRouter(Book)


// thunk sarqel, vor qashi bolor commentnery ays grqi idin hamapatasxan ev lcni stori mej
// dispatch anel ays thunk, vory ir hertin dispatch kani stori mej stacats arjeqnery lcnely

