import {combineReducers} from 'redux';
import { UPDATE_BOOKS, UPDATE_BOOK_COUNT} from './actions';

const initialState = {
    books: [],
    currentPage: 1,
    limit: 4,
    bookCount: 0,
    pageCount: 0,
    gotResponse:false
}

const booksReducer = (state = initialState, action) => {
    switch(action.type){
        case UPDATE_BOOKS:{
            return {...state, books:action.payload}   
        }
        case UPDATE_BOOK_COUNT:{
            const pageCount = Math.ceil(action.payload / state.limit)
            return {...state, bookCount:action.payload}
        }
        default: {
            return {state};
        }
       
    }
}






export const postsReducer = combineReducers({books: booksReducer});
