import { createSlice } from '@reduxjs/toolkit'
import axios from 'axios'

export const userSlice = createSlice({
    name: 'userSlice',
    initialState: {
      isAuthenticated: false,
      userId: {},
      
    }, 
    reducers: {
        saveUser: (state, action) =>{
            state.userId = action.payload
            state.isAuthenticated = true
        }
    }
})
export const sendRegistrDataThunk = (email, password) => {
    
    return () => {
        axios.post(`http://localhost:5000/api/auth/register`, {email, password})
    }
}
export const sendLoginDataThunk = (email, password) => {
    return (dispatch) => {
        axios.post(`http://localhost:5000/api/auth/login`, {email, password})
        localStorage.setItem(response.data.token)
        .then((response) => dispatch(saveUser(response.data.userId)))
      
        .catch(error => alert('Error Auth '))
       
    }
}
export const {saveUser } = userSlice.actions
export default userSlice.reducer
